import React, { useState, useEffect } from 'react';
import './App.css';
import mainHeroImage from './mainhero.jpg';
import tabletopImage from './tabletop.jpeg';
const authors = ['Elon Musk', 'Tom Brady', 'Michael Scott'];
const titles = ['How to: TTRPGs', 'Sport-themed TTRPGs', 'Master Guide: TTRPGs'];
const blurbs = ['03/17/2024', '03/08/2024', '03/10/2024'];

function Resources() {
  const [index, setIndex] = useState(0);
  const [showMainImage, setShowMainImage] = useState(true);

  useEffect(() => {
    setIndex(Math.floor(Math.random() * authors.length));
    setShowMainImage(Math.random() < 0.5); // Randomly set showMainImage to true or false
  }, []);

  return(
    <div className="Resources">
     <div className="resources-content">
      <div className="resources-cover">
        {showMainImage ? (
          <img src={mainHeroImage} className="cover" alt="cover"/>
        ) : (
          <img src={tabletopImage} className="cover" alt="cover"/>
        )}
      </div>
      <div className="text-content">
        <h2 className="coverauthor">{authors[index]}</h2>
        <h1 className="covertitle">{titles[index]}</h1>
        <h3 className="coverblurb">{blurbs[index]}</h3>
      </div>
     </div>
    </div>
  );
}

export default Resources;
