import React, { useState, useEffect } from 'react';
import './App.css';
import mainHeroImage from './mainhero.jpg';
import tabletopImage from './tabletop.jpeg';
import differentdiceImage from './differentdice.jpeg';

function Resources() {
  const authors = ['Elon Musk', 'Tom Brady', 'Michael Scott'];
  const titles = ['How to: TTRPGs', 'Sport-themed TTRPGs', 'Master Guide: TTRPGs'];
  const blurbs = ['03/17/2024', '03/08/2024', '03/10/2024'];

  const [mainImageIndex, setMainImageIndex] = useState(0);
  const [secondaryImageIndex, setSecondaryImageIndex] = useState(0);
  const [thirdImageIndex, setThirdImageIndex] = useState(0);

  useEffect(() => {
    setMainImageIndex(0); // Index for Elon Musk
    setSecondaryImageIndex(1);
    setThirdImageIndex(2);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className="Resources">
      <div className="resources-content">
        <div className="resources-cover">
          {Math.random() < 0.33 ? (
            <img src={mainHeroImage} className="cover" alt="cover" />
          ) : Math.random() < 0.66 ? (
            <img src={tabletopImage} className="cover" alt="cover" />
          ) : (
            <img src={differentdiceImage} className="cover" alt="cover" />
          )}
        </div>
        <div className="text-content">
          {Math.random() < 0.33 && (
            <div className="main-image-info">
              <h2 className="coverauthor">{authors[mainImageIndex]}</h2>
              <h1 className="covertitle">{titles[mainImageIndex]}</h1>
              <h3 className="coverblurb">{blurbs[mainImageIndex]}</h3>
            </div>
          )}
          {Math.random() >= 0.33 && Math.random() < 0.66 && (
            <div className="secondary-image-info">
              <h2 className="coverauthor">{authors[secondaryImageIndex]}</h2>
              <h1 className="covertitle">{titles[secondaryImageIndex]}</h1>
              <h3 className="coverblurb">{blurbs[secondaryImageIndex]}</h3>
            </div>
          )}
          {Math.random() >= 0.66 && (
            <div className="third-image-info">
              <h2 className="coverauthor">{authors[thirdImageIndex]}</h2>
              <h1 className="covertitle">{titles[thirdImageIndex]}</h1>
              <h3 className="coverblurb">{blurbs[thirdImageIndex]}</h3>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Resources;
